import { useState, useEffect } from "react";
import axios from "axios";

function useFlip() {
  const [isFacingUp, setIsFacingUp] = useState(true);
  const flipCard = () => setIsFacingUp(isUp => !isUp);
  return [isFacingUp, flipCard];
}

async function useAxios({name = null}) {
  const [response, setResponse] = useState(null);
  let url;
  if (name !== null) {
     url = `https://pokeapi.co/api/v2/pokemon/${name}/`;
  } else {
    url = "https://deckofcardsapi.com/api/deck/new/draw/";
  }


  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get(url);
        setResponse(res);
      } catch (error) {

      }
    };
    fetchData();
  }, [name, url]);
  return [response.data, setResponse];
}

const usePokemonList = async () => {
  const [pokemonList, setPokemonList] = useState([]);

  const fetchAndCopyPokemonNames = async () => {
    try {
      const response = await axios.get('https://pokeapi.co/api/v2/pokemon?limit=1025');
      const pokemonData = response.data.results;
      const names = pokemonData.map(pokemon => pokemon.name).sort();
      setPokemonList(names);
    } catch (error) {
      console.error('Error fetching Pokemon data:', error);
    }
  };

  useEffect(() => {
    fetchAndCopyPokemonNames();
  }, []);

  return pokemonList;
};

export { useFlip, useAxios, usePokemonList};
